import './CheckoutScreen.css'
import {useState} from 'react'

function Checkout(){

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [phone, setPhone] = useState('')
    const [street, setStreet] = useState('')
    const [number, setNumber] = useState('')
    const [zipCode, setZipCode] = useState('')
    const [city, setCity] = useState('')
    const [cardHolder, setCardHolder] = useState('')
    const [cardNumber, setCardNumber] = useState('')
    const [expiry, setExpiry] = useState(new Date())
    const [cvv, setCvv] = useState('')

    const onChangeName = (e) => {
        setName(e.target.value)
    }

    const onChangeEmail = (e) => {
        setEmail(e.target.value)
    }

    const onChangePhone = (e) => {
        setPhone(e.target.value)
    }

    const onChangeStreet = (e) => {
        setStreet(e.target.value)
    }

    const onChangeNumber = (e) => {
        setNumber(e.target.value)
    }

    const onChangeZipCode = (e) => {
        setZipCode(e.target.value)
    }

    const onChangeCity = (e) => {
        setCity(e.target.value)
    }

    const onChangeCardHolder = (e) => {
        setCardHolder(e.target.value)
    }

    const onChangeCardNumber = (e) => {
        setCardNumber(e.target.value)
    }

    const onChangeExpiry = (e) => {
        setExpiry(e.target.value)
    }

    const onChangeCvv = (e) => {
        setCvv(e.target.value)
    }

    return(
        <div className='checkout'>
            <div className='personal-info'>
                <form>
                    <div>
                        <label htmlFor='name'>Name:</label>
                        <input type='text' placeholder='name' value={name} required onChange={onChangeName}/>

                        <label htmlFor='email'>Email:</label>
                        <input type='email' placeholder='email' value={email} required onChange={onChangeEmail}/>
                    </div>

                    <div>

                    </div>

                    <div>
                        <label htmlFor='phone'>Phone:</label>
                        <input type='cel' placeholder='phone' value={phone} required onChange={onChangePhone}/>
                    </div>

                   <div>
                        <label htmlFor='street'>Street:</label>
                        <input type='text' placeholder='street' value={street} required onChange={onChangeStreet}/>
                   </div>

                    <div className='number-and-zip-code'>
                        <label htmlFor='number'>Number:  </label>
                        <input type='number' placeholder='number' value={number} required onChange={onChangeNumber}/>

                        <label htmlFor='zip-code'>Zip code:   </label>
                        <input type='number' placeholder='zip-code' value={zipCode} required onChange={onChangeZipCode}/>
                    </div>

                    <div>
                        <label htmlFor='street'>City:</label>
                        <input type='text' placeholder='city' value={city} required onChange={onChangeCity}/>
                    </div>
                </form>
            </div>

            <div className='card-info'>
                <form>
                    <div>
                        <label htmlFor='card-holder'>Card holder:</label>
                        <input type='text' placeholder='card holder' value={cardHolder} required onChange={onChangeCardHolder}/>
                    </div>

                    <div>
                        <label htmlFor='name'>Card number:</label>
                        <input type='number' placeholder='card number' value={cardNumber} required onChange={onChangeCardNumber}/>
                    </div>

                    <div className='expiry-and-cvv'>
                        <label htmlFor='name'>Expiry:   </label>
                        <input type='date' placeholder='expiry' value={expiry} required onChange={onChangeExpiry}/>

                        <label htmlFor='name'>CVV:   </label>
                        <input type='number' placeholder='CVV' value={cvv} required onChange={onChangeCvv}/>
                    </div>

                    <button className='send-form'>Buy</button>

                </form>
            </div>
        </div>
    )
}

export default Checkout
