import './CheckoutScreen.css'
import {useState, useEffect} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router-dom'
import orderNumbers from '../orderNumbers'
import * as emailjs from 'emailjs-com'
import env from 'react-dotenv'

function Checkout({cart}){

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [phone, setPhone] = useState('')
    const [street, setStreet] = useState('')
    const [number, setNumber] = useState('')
    const [zipCode, setZipCode] = useState('')
    const [cardHolder, setCardHolder] = useState('')
    const [cardNumber, setCardNumber] = useState('')
    const [expiry, setExpiry] = useState(new Date())
    const [cvv, setCvv] = useState('')

    const [qtyInCart, setQtyInCart] = useState('')
    const [totalSum, setTotalSum] = useState('')

    useEffect(() => {
        let sum = 0;
        let items = 0
        cart.forEach((item) => {
            items += item.qty;
            sum += item.qty*item.price
        }
            )
        setQtyInCart(items);
        setTotalSum(sum)
    },[cart, qtyInCart])

    const onChangeName = (e) => {
        setName(e.target.value)
    }

    const onChangeEmail = (e) => {
        setEmail(e.target.value)
    }

    const onChangePhone = (e) => {
        setPhone(e.target.value)
    }

    const onChangeStreet = (e) => {
        setStreet(e.target.value)
    }

    const onChangeNumber = (e) => {
        setNumber(e.target.value)
    }

    const onChangeZipCode = (e) => {
        setZipCode(e.target.value)
    }

    const onChangeCardHolder = (e) => {
        setCardHolder(e.target.value)
    }

    const onChangeCardNumber = (e) => {
        setCardNumber(e.target.value)
    }

    const onChangeExpiry = (e) => {
        setExpiry(e.target.value)
    }

    const onChangeCvv = (e) => {
        setCvv(e.target.value)
    }

    const paymentProcessment = (e) => {
        e.preventDefault()        

        let newOrderNumber = function(){
            let max=99999999
            let min=10000000
            function generate(max, min){
                return Math.floor(Math.random()*(max-min))+min
            }
            let chosenNumber = generate(max, min)

            for (let i=0; i<=orderNumbers.length; i++){
                if (i === orderNumbers.length){return chosenNumber}
                if (chosenNumber === orderNumbers[i]){newOrderNumber()}
            }
        }

        emailjs.sendForm('gmail', 'template_jit7267','user_ei2HwlX5HUA6Vb2BoNWPR').then((result) => {
            console.log(result)
        }, (error) => {
            console.log(error)
        })

    }


    return(
        <div>
            {cart.length ===0 ? 

            <div className='zero-cart'>
                <h1>You don't have any item in your cart</h1>
                <Link to='/products'>
                    <button>Search for products</button>
                </Link>
            </div>
            : 
            <div className='checkout'>
                <div className='informations'>

                    <div className='personal-info'>
                    <form onSubmit={(e)=>paymentProcessment(e)}>
                        {/* full name, cellphone, email, house street, house number, zip code */}
                        <h1>Personal information</h1>
                        <div className='name'>
                            <label>Name:</label>
                            <input type='text' onChange={(e) => onChangeName(e)} value={name}/>
                        </div>
                        <div className='phone'>
                            <label>Phone:</label>
                            <input type='tel' onChange={onChangePhone} value={phone}/>
                        </div>
                        <div className='email'>
                            <label>Email:</label>
                            <input type='email' onChang={onChangeEmail} value={email}/>
                        </div>
                        <div className='house-street'>
                            <label>House street:</label>
                            <input type='text' onChange={onChangeStreet} value={street}/>
                        </div>
                        <div className='house-number'>
                            <label>House number:</label>
                            <input type='number' onChange={onChangeNumber} value={number}/>
                        </div>
                        <div className='zip-code'>
                            <label>Zip code:</label>
                            <input type='number' onChange={onChangeZipCode} value={zipCode}/>
                        </div>
                        <button type='submit' id='summary-button'>Pay now</button>
                    </form>
                    </div>

                    <div className='card-info'>
                        {/* Card holder, card number, expiry date, CVV */}
                        <form>
                            <h1>Card information</h1>
                            <div className='card-holder'>
                                <label>Card holder:</label>
                                <input type='text' onChange={onChangeCardHolder} value={cardHolder}/>
                            </div>
                            <div className='card-number'>
                                <label>Card number:</label>
                                <input type='number' onChange={onChangeCardNumber} value={cardNumber}/>
                            </div>
                            <div className='expiry'>
                                <label>Expiry date:</label>
                                <input type='number' onChange={onChangeExpiry} value={expiry}/>
                            </div>
                            <div className='cvv'>
                                <label>CVV:</label>
                                <input type='number' onChange={onChangeCvv} value={cvv}/>
                            </div>
                        </form>
                    </div>
                </div>

                <div className='summary-and-pay'>
                    <div className='checkout-summary'>
                        <h2 id='summary-title'>CHECKOUT SUMMARY</h2>
                        {cart.map(item => {
                            return <div className='summary-item'>
                                <h3>{item.name} x {item.qty}</h3>
                                <h3 id='item-price'>£{item.price*item.qty}</h3>
                            </div>
                        })}
                    </div>
                    <h2 id='summary-total'>Total: £{totalSum}</h2>

                    </div>
        </div>}

        </div>
    )
}

const mapStateToProps = state => {
    return {
        cart: state.shop.cart
    }
}

export default connect (mapStateToProps) (Checkout)
