import './CartItem.css'
import {adjustQty} from './redux/Shopping/shopping-actions'
import {useState} from 'react'
import {connect} from 'react-redux'

function CartItem({data}){

    const [input, setInput, adjustQty] = useState(data.qty)

    const onChangeInput = (e, id) => {
        setInput(e.target.value);
        adjustQty(id, input)
    }

    return (
        <div>
            <div className='items'>
                <img src={data.imageUrl} alt={data.name}/>
                <div>
                    <h2>{data.name}</h2>
                    <h2>{data.price}</h2>
                    Qty: <input type='number' min='1' max={data.stock}
                        onChange={(e) => onChangeInput(e, data.id)} value={input}/>
                </div>
            </div>
            <button className='delete-button'>X</button>
        </div>
    )
}

const mapDispatchToProps = (dispatch) => {
    return {
        adjustQty: (id, value) => dispatch(adjustQty(id, value))
    }
}

export default connect (mapDispatchToProps)(CartItem)
